definition.script:
#!/usr/bin/env groovy

pipeline {
    agent any
    stages {
        stage("Build Code") {
            steps {
                build(job: "BuildIgniteSourceCode-2.5",
                      parameters:
                        [string(name: 'BRANCH_NAME', value: "${params.BRANCH_NAME}")]
                )
            }
        }
        stage("Run Tests") {
           when {
                expression {
                    "${params.Run_UnitTests}" == "true"
                }
            }            
            steps {
               build(job: "RunIgniteCoreBasicUnitTests-2.5", propagate: false)
            }
        }
        stage("Build Assembly") {
            steps {
                build("BuildIgniteAssembly-2.5")
            }
        }
        stage("Build Docker Image") {
            steps {
                build("BuildIgniteDockerImage-2.5")
            }
        }
        stage("Validate Docker Container") {
            steps {
                build("ValidateIgniteDockerContainer-2.5")
            }
        }        
        stage("Publish Docker Image") {
            steps {
                build(job: "PushIgniteDockerImage-2.5",
                      parameters:
                        [string(name: 'DOCKER_IMAGE_NAME', value: "${params.DOCKER_IMAGE_NAME}")]                
                )
            }
        }        
    }
}
